import React from "react";
import { useState } from "react";
import "./weddingStyles.css";
import Header from "./Header";
import a1 from "../img/Animals/1.jpg"
import a2 from "../img/Animals/2.jpg"
import a3 from "../img/Animals/3.jpg"
import a4 from "../img/Animals/4.jpg"
import a5 from "../img/Animals/5.jpg"
import a6 from "../img/Animals/6.jpg"
import a7 from "../img/Animals/7.jpg"
import a8 from "../img/Animals/8.jpg"
import a9 from "../img/Animals/9.jpg"
import a10 from "../img/Animals/10.jpg"
import a11 from "../img/Animals/11.jpg"
import a12 from "../img/Animals/12.jpg"
interface Image {
  src: string;
  alt: string;
}
const images: Image[] = [
  { src: {}, alt: "Image 01" },
  { src: "https://picsum.photos/id/11/1000/1000", alt: "Image 02" },
  { src: "https://picsum.photos/id/12/1000/1000", alt: "Image 03" },
  { src: "https://picsum.photos/id/13/1000/1000", alt: "Image 04" },
  { src: "https://picsum.photos/id/14/1000/1000", alt: "Image 05" },
  { src: "https://picsum.photos/id/15/1000/1000", alt: "Image 06" },
  { src: "https://picsum.photos/id/16/1000/1000", alt: "Image 07" },
  { src: "https://picsum.photos/id/17/1000/1000", alt: "Image 08" },
  { src: "https://picsum.photos/id/18/1000/1000", alt: "Image 09" },
  { src: "https://picsum.photos/id/25/1000/1000", alt: "Image 10" },
  { src: "https://picsum.photos/id/29/1000/1000", alt: "Image 11" },
  { src: "https://picsum.photos/id/46/1000/1000", alt: "Image 12" }
];


const Wedding = () => {
  const [lightboxImage, setLightboxImage] = useState("");
  const [lightboxAltImage, setLightboxAltImage] = useState("");

  const openLightbox = (src: string, alt: string) => {
    setLightboxImage(src);
    setLightboxAltImage(alt);
  };

  const closeLightbox = () => {
    setLightboxImage("");
    setLightboxAltImage("");
  };

  return (
    <>
    <Header/>
    <div>
      <div className="gallery">
        {images.map((image, index) => (
          <button
            type="button"
            className="gallery__item"
            key={index}
            onClick={() => openLightbox(image.src, image.alt)}
          >
            <img className="gallery__image" src={image.src} alt={image.alt} />
          </button>
        ))}
      </div>

      {lightboxImage && (
        <div className="lightbox">
          <button
            type="button"
            className="lightbox__close-button"
            onClick={closeLightbox}
          >
            &times;
          </button>
          <img
            className="lightbox__image"
            src={lightboxImage}
            alt={lightboxAltImage}
          />
          <button
            type="button"
            className="lightbox__bg"
            onClick={closeLightbox}
          />
        </div>
      )}
    </div>
    </>
  );
};

export default Wedding;
